Ответ:

```python
import re

def extract_numerical_features(text):
    features = []

    # Extracting mass fraction of elements
    element_mass_fractions = re.findall(r'(\w+) \((\d+\.\d+)\%\)', text)
    for element, percentage in element_mass_fractions:
        features.append({"вещество": element, "массовая доля": [float(percentage[:-1]), None]})

    # Extracting quantity of goods
    quantities = re.findall(r'(\d+(?:\.\d*)?)', text)
    if 'кг' in quantities or 'тонна' in quantities:
        features.append({"параметр": "количество", "количество": float(''.join(filter(str.isdigit, ''.join(quantities))) or 0)})
    elif 'шт' in quantities:
        features.append({"параметр": "количество поддонов", "количество": int(''.join(filter(str.isdigit, ''.join(quantities))) or 0)})
    elif 'мешка' in quantities:
        features.append({"параметр": "количество мешков", "количество": int(''.join(filter(str.isdigit, ''.join(quantities))) or 0)})
    elif 'п/э мешка' in quantities:
        features.append({"параметр": "количество п/э мешков", "количество": int(''.join(filter(str.isdigit, ''.join(quantities))) or 0)})
    elif 'вагон' in quantities:
        features.append({"параметр": "количество вагонов", "количество": int(''.join(filter(str.isdigit, ''.join(quantities))) or 0)})

    # Extracting other numerical values
    other_numerical_values = re.findall(r'(\w+) (\d+)(?:\s+-\s+(\d+))?', text)
    for value, min_value, max_value in other_numerical_values:
        if min_value is not None and max_value is not None:
            features.append({"параметр": f"{value} {min_value}-{max_value}", f"{value}": [int(min_value), int(max_value)]})
        else:
            features.append({"параметр": f"{value} {min_value}" if min_value is not None else f"{value} {max_value}" if max_value is not None else "", f"{value}": [int(min_value) if min_value is not None else None, int(max_value) if max_value is not None else None]})

    return features


